import { useEffect, useState, useRef } from 'react';
import './App.css';

function maxIdInArray(arr) {
  return Math.max(...arr.map((el) => +el.id));
}

function App() {
  const [todos, setTodos] = useState([]);
  const [submit, setSubmit] = use

  const inputRef = useRef();

  useEffect(() => {
    fetch('http://localhost:3003/todos')
      .then((data) => data.json())
      .then((todos) => {
        setTodos(todos);
      })
      .catch((error) => console.log(error));
  }, [todos]);



  function handleTaskSubmit(e) {
    e.preventDefault();

    fetch('http://localhost:3003/todos', {
      method: 'POST',
      headers: { 'Content-Type': 'application.json' },
      body: JSON.stringify({
        id: +`${maxIdInArray(todos)}` + 1 + '' ,
        title: `${inputRef.current.value}`,
        status: false,
      }),
    });
    inputRef.current.value = ''
  }

  return (
    <main className="main">
      <h1>Your toDos</h1>
      <form onSubmit={handleTaskSubmit} className="form">
        <input ref={inputRef} type="text" placeholder="Enter your task" />
        <button>Add</button>
      </form>
      <div className="tasks">
        {todos.map((todo) => (
          <div className="task">
            <h3 key={todo.id}>{todo.title}</h3>
            <button>Delete</button>
          </div>
        ))}
      </div>
    </main>
  );
}

export default App;
