import { useEffect, useState, useRef } from 'react';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEnvelope } from '@fortawesome/free-solid-svg-icons'

function maxIdInArray(arr) {
  return Math.max(...arr.map((el) => +el.id));
}

function App() {
  const [todos, setTodos] = useState([]);
  const [submit, setSubmit] = useState(false);

  const inputRef = useRef();

  useEffect(() => {
    fetch('http://localhost:3003/todos')
      .then((data) => data.json())
      .then((todos) => {
        setTodos(todos);
      })
      .catch((error) => console.log(error));
  }, [todos]);

  function handleTaskSubmit(e) {
    e.preventDefault();

    fetch('http://localhost:3003/todos', {
      method: 'POST',
      headers: { 'Content-Type': 'application.json' },
      body: JSON.stringify({
        id: todos.length === 0 ? '1' : +`${maxIdInArray(todos)}` + 1 + '',
        title: `${inputRef.current.value}`,
        status: false,
      }),
    });
    inputRef.current.value = '';
    setSubmit(!submit);
  }

  function handleAreaChange(id, target) {
    const value = target.value;
    fetch(`http://localhost:3003/todos/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application.json' },
      body: JSON.stringify({
        id: id,
        title: value,
        status: false,
      }),
    });
  }

  function handleTaskDelete(id) {
    fetch(`http://localhost:3003/todos/${id}`, {
      method: 'DELETE',
    });
    setSubmit(!submit);
  }

  return (
    <main className="main">
      <FontAwesomeIcon icon={faEnvelope} />
      <FontAwesomeIcon icon="fa-solid faMagnifying-glass" />
      <h1>Your toDos</h1>
      <form onSubmit={handleTaskSubmit} className="form">
        <input ref={inputRef} type="text" placeholder="Enter your task" />
        <button>Add</button>
      </form>
      <div className="tasks">
        {todos.map((todo) => (
          <div className="task">
            <textarea
              value={todo.title}
              key={todo.id}
              onChange={({ target }) => handleAreaChange(todo.id, target)}
            />
            <button onClick={() => handleTaskDelete(todo.id)}>Delete</button>
          </div>
        ))}
      </div>
    </main>
  );
}

export default App;
