export const initialTodosState = [];

export const todosReducer = (state = initialTodosState, action) => {
  switch (action.type) {
    case 'SET_TODOS': {
      return action.payload;
    }
    case 'ADD_TASK':      
      return [...state, action.payload];
    case 'DELETE_TASK': {
      return state.filter((el) => el)
    }
    default:
      return state;
  }
};
