import { useRef, useEffect } from 'react';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import { useDispatch, useSelector } from 'react-redux';
import { setTodos, setSubmit } from './redux/actions';
import { handleSearchChange, handleSortTasks } from './redux/functions';

function maxIdInArray(arr) {
  return Math.max(...arr.map((el) => +el.id));
}

function App() {
  const submit = useSelector((state) => state.submit);
  const searchTask = useSelector((state) => state.searchTask);
  const todos = useSelector((state) => state.todos);
  const inputRef = useRef();
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(setTodos());
  }, [dispatch, submit]);

  function handleTaskSubmit(e) {
    e.preventDefault();
    const newTask = {
      id: todos.length === 0 ? '1' : `${Math.max(...todos.map((t) => +t.id)) + 1}`,
      title: inputRef.current.value,
      status: false,
    };
    inputRef.current.value = '';
    dispatch(setSubmit(!submit));
  }

  function handleAreaChange(id, target) {
    const value = target.value;
    fetch(`http://localhost:3003/todos/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application.json' },
      body: JSON.stringify({
        id: id,
        title: value,
        status: false,
      }),
    });
  }

  function handleTaskDelete(id) {
    fetch(`http://localhost:3003/todos/${id}`, {
      method: 'DELETE',
    });
    dispatch(setSubmit(!submit));
  }

  const filteredTodos = todos.filter((todo) =>
    todo.title.toLowerCase().includes(searchTask.toLowerCase())
  );

  return (
    <main className="main">
      <h1>Your toDos</h1>
      <form onSubmit={handleTaskSubmit} className="form">
        <input ref={inputRef} type="text" placeholder="Enter your task" />
        <button>Add Task</button>
      </form>

      <div className="search">
        <input
          type="text"
          placeholder="Search for a task"
          onChange={(e) => handleSearchChange(dispatch, e.target.value)}
        />
        <button className="searchBtn">
          <FontAwesomeIcon icon={faMagnifyingGlass} />
        </button>
        <button onClick={() => handleSortTasks(todos, dispatch)}>
          Sort tasks
        </button>
      </div>

      <div className="tasks">
        {filteredTodos.map((todo) => (
          <div className="task" key={todo.id}>
            <textarea
              value={todo.title}
              onChange={({ target }) => handleAreaChange(todo.id, target)}
            />
            <button onClick={() => handleTaskDelete(todo.id)}>Delete</button>
          </div>
        ))}
      </div>
    </main>
  );
}

export default App;
