import { useEffect, useState, useRef } from 'react';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';

function maxIdInArray(arr) {
  return Math.max(...arr.map((el) => +el.id));
}

function App() {
  const [todos, setTodos] = useState([]);
  const [submit, setSubmit] = useState(false);
  const [searchTask, setSearchTask] = useState('');

  const inputRef = useRef();

  useEffect(() => {
    fetch('http://localhost:3003/todos')
      .then((data) => data.json())
      .then((todos) => {
        setTodos(todos);
      })
      .catch((error) => console.log(error));
  }, [submit]);

  function handleTaskSubmit(e) {
    e.preventDefault();

    fetch('http://localhost:3003/todos', {
      method: 'POST',
      headers: { 'Content-Type': 'application.json' },
      body: JSON.stringify({
        id: todos.length === 0 ? '1' : +`${maxIdInArray(todos)}` + 1 + '',
        title: inputRef.current.value,
        status: false,
      }),
    });
    inputRef.current.value = '';
    setSubmit(!submit);
  }

  function handleAreaChange(id, target) {
    const value = target.value;
    fetch(`http://localhost:3003/todos/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application.json' },
      body: JSON.stringify({
        id: id,
        title: value,
        status: false,
      }),
    });
  }

  function handleTaskDelete(id) {
    fetch(`http://localhost:3003/todos/${id}`, {
      method: 'DELETE',
    });
    setSubmit(!submit);
  }

  function handleSearchChange(e) {
    setSearchTask(e.target.value);
  }

  const filteredTodos = todos.filter((todo) =>
    todo.title.toLowerCase().includes(searchTask.toLowerCase())
  );

  console.log(todos);

  function handleSortTasks() {
    const sortedTodos = [...todos].sort((a, b) => {
      if (a.title.toLowerCase() < b.title.toLowerCase()) return -1;
      if (a.title.toLowerCase() > b.title.toLowerCase()) return 1;
      return 0;
    });
    setTodos(sortedTodos);
  }



  return (
    <main className="main">
      <h1>Your toDos</h1>
      {/* add a new task form */}
      <form onSubmit={handleTaskSubmit} className="form">
        <input ref={inputRef} type="text" placeholder="Enter your task" />
        <button>Add Task</button>
      </form>
      {/* end of new task from */}

      {/* search area */}
      <div className="search">
        <input
          type="text"
          placeholder="Search for a task"
          onChange={handleSearchChange}
        />
        <button className="searchBtn">
          <FontAwesomeIcon icon={faMagnifyingGlass} />
        </button>
        <button onClick={handleSortTasks}>Sort tasks</button>
      </div>
      {/* end of search area */}

      {/* mapping todos */}
      <div className="tasks">
        {filteredTodos.map((todo) => (
          <div className="task" key={todo.id}>
            <textarea
              value={todo.title}
              onChange={({ target }) => handleAreaChange(todo.id, target)}
            />
            <button onClick={() => handleTaskDelete(todo.id)}>Delete</button>
          </div>
        ))}
      </div>
      {/* end of mapping */}
    </main>
  );
}

export default App;
